import React, { useEffect, useState } from 'react';
import Swal from 'sweetalert2';
import axios from 'axios';

const Employes = () => {
  // État pour les employés
  const [users, setUsers] = useState([]);

  // État pour les groupes et modal d'affectation de groupe
  const [groupes, setGroupes] = useState([]);
  const [selectedGroupe, setSelectedGroupe] = useState('');
  const [showModalgr, setShowModalgr] = useState(false);

  // État pour le modal d'ajout d'un employé
  const [showModal, setShowModal] = useState(false);

  // Récupérer les employés
  useEffect(() => {
    loadUsers();
  }, []);

  const loadUsers = async () => {
    const result = await axios.get("http://localhost:8080/api/employe");
    setUsers(result.data);
  };

  // Récupérer les groupes
  useEffect(() => {
    const fetchGroupes = async () => {
      try {
        const response = await fetch('http://localhost:8080/api/groupes');
        const data = await response.json();
        setGroupes(data);
      } catch (error) {
        console.error('Erreur lors du fetch des groupes:', error);
      }
    };
    fetchGroupes();
  }, []);

  // Soumettre le formulaire d'affectation de groupe
  const handleSubmitGroup = (e) => {
    e.preventDefault();
    console.log('Groupe sélectionné:', selectedGroupe);
    setShowModalgr(false); // Fermer le modal après soumission
  };

  // Fermer le popup de suppression du client
  const handleDeleteClient = (clientId) => {
    Swal.fire({
      title: 'Êtes-vous sûr?',
      text: "Cette action est irréversible!",
      icon: 'warning',
      showCancelButton: true,
      confirmButtonColor: '#3085d6',
      cancelButtonColor: '#d33',
      confirmButtonText: 'Oui, supprimer!',
    }).then((result) => {
      if (result.isConfirmed) {
        console.log("Client supprimé:", clientId);
        Swal.fire('Supprimé!', 'Le client a été supprimé.', 'success');
      }
    });
  };

  return (
    <>
      {/* Bouton pour ajouter un employé */}
      <div className="flex justify-end">
        <button
          className="p-2 my-12 mx-12 rounded-full text-white font-bold bg-blue-500 group transition-all duration-500 hover:bg-blue-600 flex items-center"
          onClick={() => setShowModal(true)}
        >
          <span className="mr-2">Ajouter un Employe</span>
          <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={2} stroke="currentColor" className="w-5 h-5">
            <path strokeLinecap="round" strokeLinejoin="round" d="M12 4.5v15m7.5-7.5h-15" />
          </svg>
        </button>

        {/* Modal d'ajout d'un employé */}
        {showModal && (
          <div className="fixed inset-0 flex items-center justify-center z-50 bg-black bg-opacity-50">
            <div className="bg-white rounded-lg p-6 w-96 shadow-lg">
              <h2 className="text-lg font-bold mb-4">Ajouter un Employe</h2>
              <form>
                <div className="mb-4">
                  <label className="block text-gray-700 font-bold mb-2">Nom d'Employe</label>
                  <input
                    type="text"
                    className="w-full p-2 border rounded-lg"
                    placeholder="Entrez le Nom du client"
                  />
                </div>
                <div className="flex justify-end">
                  <button
                    type="button"
                    className="mr-2 px-4 py-2 bg-gray-300 text-gray-700 rounded-lg"
                    onClick={() => setShowModal(false)}
                  >
                    Annuler
                  </button>
                  <button
                    type="submit"
                    className="px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600"
                  >
                    Ajouter
                  </button>
                </div>
              </form>
            </div>
          </div>
        )}
      </div>

      {/* Tableau des employés */}
      <div className="flex flex-col w-[65%] mx-auto">
        <div className="overflow-x-auto">
          <div className="min-w-full inline-block align-middle">
            <div className="overflow-hidden border rounded-lg border-gray-300">
              <table className="min-w-full rounded-xl">
                <thead>
                  <tr className="bg-gray-800 text-white">
                    <th className="p-5 text-left text-sm font-semibold capitalize">Code Employe</th>
                    <th className="p-5 text-left text-sm font-semibold capitalize">Nom Employe</th>
                    <th className="p-5 text-left text-sm font-semibold capitalize">Action</th>
                    <th className="p-5 text-left text-sm font-semibold capitalize flex items-center justify-center">Affecter Groupe</th>
                  </tr>
                </thead>
                <tbody className="divide-y divide-gray-300">
                  {users.map((user, index) => (
                    <tr key={index}>
                      <td className="p-5 text-sm text-gray-900">{user.codeEmploye}</td>
                      <td className="p-5 text-sm text-gray-900">{user.nomEmploye}</td>
                      <td className="p-5 text-sm text-gray-900 flex gap-2">
                        <button
                          onClick={() => handleDeleteClient(user.codeEmploye)}
                          className="p-2 rounded-full bg-white group transition-all duration-500 hover:bg-red-600 flex items-center"
                        >
                          <svg className="w-5 h-5 text-red-500 group-hover:text-white" fill="currentColor" viewBox="0 0 20 20">
                            <path d="M6 7v10h8V7H6zm8-3V3a1 1 0 0 0-1-1H7a1 1 0 0 0-1 1v1H2v2h16V4h-4z" />
                          </svg>
                        </button>
                      </td>
                      <td className="flex justify-center">
                        <button
                          className="p-2 mx-12 rounded-full text-white font-bold bg-blue-500 group transition-all duration-500 hover:bg-blue-600 flex items-center"
                          onClick={() => setShowModalgr(true)}
                        >
                          Affecter
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      </div>

      {/* Modal pour affecter un groupe */}
      {showModalgr && (
        <div className="fixed inset-0 flex items-center justify-center z-50 bg-black bg-opacity-50">
          <div className="bg-white p-6 rounded-lg shadow-lg w-96">
            <h2 className="text-xl font-bold mb-4">Sélectionner un Groupe</h2>
            <form onSubmit={handleSubmitGroup}>
              <div className="mb-4">
                <label htmlFor="groupe" className="block mb-2 font-semibold">Choisir un Groupe</label>
                <select
                  id="groupe"
                  value={selectedGroupe}
                  onChange={(e) => setSelectedGroupe(e.target.value)}
                  className="w-full p-2 border rounded"
                  required
                >
                  <option value="">-- Sélectionner un Groupe --</option>
                  {groupes.map((groupe) => (
                    <option key={groupe.id} value={groupe.id}>
                      {groupe.nom}
                    </option>
                  ))}
                </select>
              </div>

              <div className="flex justify-end">
                <button
                  type="button"
                  onClick={() => setShowModalgr(false)}
                  className="px-4 py-2 bg-gray-300 rounded hover:bg-gray-400 mr-2"
                >
                  Annuler
                </button>
                <button
                  type="submit"
                  className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600"
                >
                  Affecter
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </>
  );
};

export default Employes;
